import React, { useState } from "react";

const MyAccordion = ({ question, answer }) => {

    const [show, setShow] = useState(false);

    return (
        <>
            <div className="main-heading">
                <p onClick={() => setShow(!show)}>+  <h3> {question} </h3> </p>

            </div>
            {show && <p> {answer} </p>}
        </>
    );
};

export default MyAccordion;
